<aside class='widget widget_contact'>
	<form action='' class='form-contact' method='post'>
		<fieldset>
			<legend class='widget_title'>Dúvidas? Fale Conosco!</legend>
			<p class='field'>
				<input aria-label='Nome' aria-required='true' id='name' name='' placeholder='Nome:' required type='text'>
			</p>
			<p class='field'>
				<input aria-label='Telefone' aria-required='true' id='phone' name='' placeholder='Telefone:' required type='text'>
			</p>
			<p class='field'>
				<input aria-label='E-mail' aria-required='true' id='email' name='' placeholder='E-mail:' required type='text'>
			</p>
			<p class='field'>
				<textarea aria-label='Mensagem ou dúvida' aria-required='true' cols='30' id='msg' name='' placeholder='Mensagem ou dúvida:' required rows='8'></textarea>
			</p>
			<p class='submit'>
				<button class='button' type='submit'>Enviar</button>
			</p>
		</fieldset>
	</form>
</aside>
